windowlist.script:
function addEntry(sNode, sToken, sName, nXp)
	local w = createWindow();
	if w then
		w.node.setValue(sNode);
		w.token.setPrototype(sToken);
		w.name.setValue(sName .. " (" .. nXp .. ")");
	end
	return w;
end
function update()
	for _, w in ipairs(getWindows()) do
		w.updateFrame(window.selected.getValue());
	end
end

button_text.script:
function onInit()
	if super and super.onInit then
		super.onInit();
	end
	updateTooltip();
end
function onSelectionChanged()
	updateTooltip();

	if not isVisible() then
		setVisible(true);
	end
end
function updateTooltip()
	if (window.selected.getValue() or "") == "" then
		setTooltipText(Interface.getString("gmi_prompt_tooltip_select_char"))
	else
		setTooltipText(string.format(
			Interface.getString("gmi_prompt_tooltip_accept"),
			window.selectedname.getValue()))
	end
end
function onButtonPress()
	window.acceptIntrusion();
end

windowclass.script:
function onInit()
	updateFrame();
end
function updateFrame(sSelectedNode)
	if sSelectedNode == node.getValue() then
		setFrame("ctentrybox_friend");
	else
		setFrame("ctentrybox_neutral");
	end
end

function onSelected()
	windowlist.window.onSelect(node.getValue(), name.getValue());
end

tokencontrol.script:
function onClickDown(button, x, y)
	return button == 1;
end
function onClickRelease(button, x, y)
	window.onSelected();
end

label.script:
function onClickDown(button, x, y)
	return button == 1;
end
function onClickRelease(button, x, y)
	window.onSelected();
end
